parameters.functionCode:
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const jobData = item.json;
    let analysis = {};

    if (item.error) {
      analysis = {
        score: 25,
        reasoning: 'AI analysis failed: ' + item.error.message,
        key_matches: [],
        missing_requirements: ['AI analysis error'],
        recommendation: 'review_manually'
      };
    } else {
      const aiResponse = item.json.message?.content || item.json.text || '';

      try {
        const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
        analysis = JSON.parse(jsonMatch ? jsonMatch[0] : aiResponse);
        analysis.score = Math.min(Math.max(analysis.score || 0, 0), 100);
      } catch (parseError) {
        analysis = {
          score: 30,
          reasoning: 'JSON parse failed: ' + parseError.message,
          key_matches: [],
          missing_requirements: ['JSON parsing error'],
          recommendation: 'review_manually'
        };
      }
    }

    results.push({
      job_id: jobData.id,
      title: jobData.title,
      company: jobData.company,
      url: jobData.url,
      score: analysis.score,
      reasoning: (analysis.reasoning || 'No reasoning').replace(/'/g, "''"),
      key_matches: JSON.stringify(analysis.key_matches || []),
      missing_requirements: JSON.stringify(analysis.missing_requirements || []),
      recommendation: analysis.recommendation || 'review',
      ai_analysis: JSON.stringify(analysis),
      analyzed_at: new Date().toISOString()
    });
  } catch (error) {
    results.push({
      job_id: item.json?.id || 0,
      score: 0,
      reasoning: 'Error: ' + error.message,
      recommendation: 'error'
    });
  }
}

return results;

